import { Grid, TextField } from '@material-ui/core';
import React from 'react';
import InputField from './Inputs/InputField';
import RadioButton from './Inputs/Radio';
import formik, { useFormik } from 'formik';

const initialValues ={
    level: "",
    english:"",
    spanish:"",
    explaination:"",
    option:{
        statement_en:"",
        statement_es:"",
        isCorrect:false,
    }

}
const InputForm = () => {
    const formik=useFormik({
        initialValues,

    })
    return ( 
        <form>
            <Grid container direction="column"  >
                <Grid item xs={12}>
              <RadioButton radioValue={["1","2","3"]} onChange={formik.handleChange} value={formik.values.level} name="level"/>

              </Grid>


              <Grid item xs={12} style={{marginTop:'10px'}}>
              <InputField label="English" type="text" onChange={formik.handleChange} value={formik.values.english} name="english"/>

              </Grid>
              <Grid item xs={12} style={{marginTop:'10px'}}>
              <InputField label="Spanish" type="text" onChange={formik.handleChange} value={formik.values.spanish} name="spanish"/>

              </Grid>

              { formik.values.level==="1" || formik.values.level==="" ?

            <div>
              <Grid item xs={12} style={{marginTop:'10px'}}>
              <RadioButton radioValue={["true","false"]} onChange={formik.handleChange} value={formik.values.level} name="level"/>


              </Grid>
              <Grid item xs={12} style={{marginTop:'10px'}}>
              <InputField label="Explaination" type="textarea" onChange={formik.handleChange} value={formik.values.explaination} name="explaination"/>

              </Grid>
              </div>:
              <div>
                  <Grid item xs={12} style={{marginTop:'10px'}}> 
                  <InputField type="text" onChange={formik.handleChange} value={formik.values.option.statement_en} name="option_1"/>
                  <RadioButton  onChange={formik.handleChange} value={formik.values.option.isCorrect} name="isCorrect"/>


                   </Grid>
                   <Grid item xs={12} style={{marginTop:'10px'}}>
              <InputField label="Explaination" type="textarea" onChange={formik.handleChange} value={formik.values.explaination} name="explaination"/>

              </Grid>
              </div>
              }


           </Grid>
        </form>
     );
}

export default InputForm;
